ZTimer.OnTimer:
//var spawned = Unit.CreateClone(EnemyUnits);
//spawned.Name = "Enemy Unit";
//spawned.Color = Color.Blue;
var spawned = Resource.CreateClone(OilFields);
spawned.Position = ZMath.RandomVector(99.0f,99.0f,0);
AdjustAltitude(spawned,-0.2f);

ZApplication.OnMouseUp:
if (e.Button == SelectButton)
{  
    UnitModel selUnit = SelectedObject as UnitModel;

    //  Do a hit test.
    ZComponent comp = MousePick(e.X, e.Y);
    if (comp != null)
    {
        //Console.WriteLine("MousePick result: {0}", comp.Name);
        UnitModel newUnit = comp as UnitModel;
        if (newUnit != null)
        {
            SelectedObject = newUnit;
            HideBuildMenu();
        }
        else if (comp == Island1)        
        {
            ShowBuildMenu(e.X, e.Y);
        }
        else if (selUnit != null)
        {
            selUnit.Destination = PickCoordinates;
            selUnit.GoToDestination = true;
        }
    }
}

ZApplication.OnMouseMove:
if (CreateCityBtn.IsHidden == false)
{
    Vector2 mpos = new Vector2(e.X, e.Y);
    float dist = (mpos - cursorRef).Length;
    if (dist > 100.0f)
        HideBuildMenu();
}
